import React, { useState } from 'react';

export default function ClientForm() {
  const [form, setForm] = useState({ nom: '', prenom: '', adresse: '', cp: '', tel: '', email: '' });
  const [error, setError] = useState('');
  const services = ['Devis', 'Entretien', 'Dépannage'];

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!/^[1-6]\d{3}$/.test(form.cp)) {
      setError('Désolé, nous ne travaillons pas dans votre zone.');
      return;
    }
    // TODO: soumettre au backend + Google Agenda
    alert(`✅ Formulaire soumis : ${JSON.stringify(form)}`);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-green-300 to-blue-300 p-6">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-2xl shadow-xl max-w-md w-full">
        <h2 className="text-3xl font-bold mb-6 text-gray-800 text-center">Formulaire Client</h2>
        {error && <p className="text-red-500 mb-4 text-center">{error}</p>}
        {['nom', 'prenom', 'adresse', 'cp', 'tel', 'email'].map((field) => (
          <label key={field} className="block mb-4">
            <span className="block text-gray-700 capitalize">{field === 'tel' ? 'Téléphone' : field.charAt(0).toUpperCase() + field.slice(1)}</span>
            <input
              type={field === 'email' ? 'email' : 'text'}
              name={field}
              value={form[field]}
              onChange={handleChange}
              required
              className="mt-1 w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-400"
            />
          </label>
        ))}
        <label className="block mb-6">
          <span className="block text-gray-700">Service souhaité</span>
          <select
            name="service"
            onChange={(e) => setForm({ ...form, service: e.target.value })}
            required
            className="mt-1 w-full border border-gray-300 px-3 py-2 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-400"
          >
            <option value="">-- Choisissez un service --</option>
            {services.map((s) => (
              <option key={s} value={s}>{s}</option>
            ))}
          </select>
        </label>
        <button type="submit" className="w-full bg-green-500 hover:bg-green-600 text-white py-3 rounded-lg font-semibold transition">Valider</button>
      </form>
    </div>
  );
}
